import React, { useState } from "react";
import CreateNote from "./CreateNote";
import Footer from "./Footer";
import Header from "./Header";
import "./Keep.css";
import Notee from "./Notee";

const Keep = () => {
  const [addItem, setaddItem] = useState([]);
  const AddE = (note) => {
    // alert("I am clicked");
    setaddItem((prev) => {
      return [...prev, note];
    });
    console.log(note);
  };
  const RemoveE = (id) => {
    setaddItem((prev) => {
      prev.filter((currD, index));
    });
  };
  return (
    <>
      <Header />
      <CreateNote passNote={AddE} />
      {/* <Notee /> */}
      {addItem.map((val, index) => {
        return (
          <Notee
            key={index}
            id={index}
            title={val.title}
            content={val.content}
            deleteItem={RemoveE}
          />
        );
      })}
      <Footer />
    </>
  );
};

export default Keep;
