  greeting = "Good Morning";
} else if (date >= 12 && date < 5) {
  greeting = "Good Evening";
}

const Challenge3 = () => {
  return (
    <>
      <h1>
        Hello sir, <span>{date}</span>
      </h1>
    </>
  );
};

export default Challenge3;
