// Increment and decrement using MUI
import React, { useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";
import Button from "@mui/material/Button";
import Clock from "react-digital-clock";

const Challengee8 = () => {
  const [count, setCount] = useState(0);

  const Incre = () => {
    setCount(count + 1);
  };
  const Decre = () => {
    if (count > 0) {
      setCount(count - 1);
    } else {
      setCount(0);
      alert("Sorry, Zero limit reached");
    }
  };
  return (
    <>
      <h1>Increment and decrement</h1>
      <h1> {count}</h1>
      {/* <button onClick={Incre}>
        <AddIcon />
      </button>{" "} */}
      <Button onClick={Incre} variant="contained" color="success">
        <AddIcon />
      </Button>{" "}
      {""}
      {/* <button onClick={Decre}>

        <RemoveIcon color="success" />
      </button> */}
      <Button onClick={Decre} variant="outlined" color="error">
        <RemoveIcon />
      </Button>
      <br />
      <hr />
      <clock />
      <Clock />
    </>
  );
};
export default Challengee8;
