import React, { useEffect, useState } from "react";

const WidthM = () => {
  const [widthc, setWidthC] = useState(window.screen.width);

  const ActWidth = () => {
    setWidthC(window.innerWidth);
    console.log(window.innerWidth);
  };
  useEffect(() => {
    console.log("add event");
    window.addEventListener("resize", ActWidth);
    return () => {
      console.log("remove event");
      window.removeEventListener("resize", ActWidth);
    };
  }, []);
  return (
    <>
      <h3>The Actual window size is : </h3>
      <h4>{widthc}</h4>
    </>
  );
};

export default WidthM;
