import React, { useReducer, useState } from "react";

const initialState = 0;

const reducer = (state, action) => {
  console.log(state, action);
  if (action.type === "Increment") {
    return state + 1;
  } else if (action.type === "Decrement") {
    return state - 1;
  } else if (action.type === initialState) {
    return initialState;
  }
  return state;
};

const CountN = () => {
  // const [count, setCount] = useState(0);
  const [state, dispatch] = useReducer(reducer, initialState);
  return (
    <>
      <p>Count - {state}</p>
      <button onClick={() => dispatch({ type: "Increment", value: 1 })}>
        Incr
      </button>
      <button onClick={() => dispatch({ type: "Decrement", value: 1 })}>
        Decr
      </button>
      <button onClick={() => dispatch({ type: "Increment" })}>Incr</button>
      <button onClick={() => dispatch({ type: "Decrement" })}>Decr</button>
      <button onClick={() => dispatch({ type: initialState })}>Reset</button>
    </>
  );
};

export default CountN;
