import React, { useState } from "react";

const TextArea = () => {
  const [textarea, setTextarea] = useState("The textarea space is the ");

  const handleChange = () => {
    setTextarea();
  };
  return (
    <>
      <h1>TextArea Form</h1>
      <form>
        <textarea value={textarea} onChange={handleChange} />
      </form>
    </>
  );
};

export default TextArea;
